    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    calcolo calcolo = new calcolo();
    int slash;
    slash = Integer.parseInt(jTextField1.getText());
    //variabile slash per usare il metodo subnet con il valore che inserir√≤ nella casella
    String subnet = calcolo.Subnet(slash);
    jTextField4.setText(subnet);
    
    int nhost = calcolo.numerohost(slash);
    jTextField2.setText(Integer.toString(nhost));
    
    int nsottoreti = calcolo.numerosottoreti(slash);
    jTextField3.setText(Integer.toString(nsottoreti));
    
    }//GEN-LAST:event_jButton1ActionPerformed

